   111	// Initialize simple language switcher
   112	function initSimpleLangSwitcher() {
   113	    const button = document.getElementById('setupLangCycler');
   114	    if (!button) {
   115	        return;
   116	    }
   117	    
   118	    // Find current language index
   119	    const currentLang = getCurrentLanguage();
   120	    currentLangIndex = LANG_ORDER.indexOf(currentLang);
   121	    if (currentLangIndex === -1) currentLangIndex = 0;
   122	    
   123	    // Update button to show current language
   124	    const config = LANG_CONFIG[LANG_ORDER[currentLangIndex]];
   125	    button.textContent = `${config.flag} ${config.name}`;
   126	    
   127	    // Translate page to current language
   128	    translatePage(LANG_ORDER[currentLangIndex]);
   129	    
   130	    // Use mousedown since click events are being blocked
   131	    button.addEventListener('mousedown', function(e) {
   132	        e.preventDefault();
   133	        e.stopPropagation();
   134	
   135	        // Cycle to next language
   136	        currentLangIndex = (currentLangIndex + 1) % LANG_ORDER.length;
   137	        const newLangCode = LANG_ORDER[currentLangIndex];
   138	        const newConfig = LANG_CONFIG[newLangCode];
   139	
   140	        // Update button immediately
   141	        button.textContent = `${newConfig.flag} ${newConfig.name}`;
   142	
   143	        // Set cookie
   144	        setLanguageCookie(newLangCode);
   145	
   146	        // Translate page
   147	        translatePage(newLangCode);
   148	
   149	        return false;
   150	    });
   151	    
   152	
   153	}
   154	
   155	// Initialize when DOM is ready
   156	if (document.readyState === 'loading') {
   157	    document.addEventListener('DOMContentLoaded', initSimpleLangSwitcher);
   158	} else {
   159	    initSimpleLangSwitcher();
   160	}
