  1105	    // Update theme observer to handle both Mermaid and GraphViz
  1106	    const observer = new MutationObserver((mutations) => {
  1107	        mutations.forEach((mutation) => {
  1108	            if (mutation.attributeName === 'data-theme') {
  1109	                if (useGraphviz && currentData) {
  1110	                    // Reset GraphViz theme cache on theme change
  1111	                    lastGraphvizTheme = null;
  1112	                    lastGraphvizResult = null;
  1113	                    renderGraphvizSchema(container, currentData);
  1114	                } else if (!useGraphviz && currentData) {
  1115	                    const isDark = document.documentElement.getAttribute('data-theme') === 'dark';
  1116	                    mermaid.initialize({
  1117	                        theme: isDark ? 'dark' : 'default'
  1118	                    });
  1119	                    renderMermaidSchema(container, currentData);
  1120	                }
  1121	            }
  1122	        });
  1123	    });
