    49	const LANG_ORDER = ['en', 'ko', 'vi'];
    50	let currentLangIndex = 0;
    51	
    52	// Get current language from cookie
    53	function getCurrentLanguage() {
    54	    const cookies = document.cookie.split(';');
    55	    for (let cookie of cookies) {
    56	        const [name, value] = cookie.trim().split('=');
    57	        if (name === 'preferred_language') {
    58	            return value;
    59	        }
    60	    }
    61	    return 'en';
    62	}
